mapper.select:
select oper_id, title, business_type, method, request_method, operator_type, oper_name, dept_name, oper_url, oper_ip, oper_location, oper_param, json_result, status, error_msg, oper_time from zo_mes.sys_oper_log
AND title like concat('%', #{title}, '%')
AND oper_name like concat('%', #{operName}, '%')
AND operator_type = #{operatorType}
AND status = #{status}
order by oper_id desc
