mapper.select:
SELECT * FROM md_client
WHERE 1=1


    AND client_code = #{customerCode}


    AND client_name = #{customerName}


    AND client_en = #{customerEnglishName}


    AND client_nick = #{customerSimpleName}



    AND (client_id = #{clientId} OR client_id in (select client_id from md_client where find_in_set(#{clientId},ancestors)))
